<script>

    $(document).ready(function() {

        $(document).on('click', '#delete_product_site', function() {

            var idProduct = $(this).data('id');
            let nameProduct = $(this).data('name');
            Swal.fire({
                title: 'Do you want to delete <div class="text-blue-500">'+ nameProduct +'?</div>',
                text: "You won't be able to revert this!",
                icon: 'warning',
                width: 800,
                showCancelButton: true,
                confirmButtonColor: '#d33',
                cancelButtonColor: '#3085d6',
                confirmButtonText: 'Delete',
                cancelButtonText: 'Cancel'
            }).then((result) => {
                if (result.isConfirmed) {
                    $.ajax({
                        url: '/admin/productSite/delete/' + idProduct,
                        type: 'DELETE',
                        headers: {
                            'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                        },
                        success: function(response) {

                            if(response == 1) {
                                Swal.fire({
                                    icon: "success",
                                    title: 'Delete <div class="text-blue-500">'+ nameProduct +'</div> successfully!',
                                    showConfirmButton: false,
                                    timer: 2000,
                                    timerProgressBar: true,
                                });
                            } else {
                                Swal.fire({
                                    icon: "error",
                                    title: 'Delete <div class="text-blue-500">'+ nameProduct +'</div> failed!',
                                    showConfirmButton: false,
                                    timer: 2000,
                                    timerProgressBar: true,
                                });
                            }

                            timeOut = setTimeout(function() {
                                window.location.reload();
                            }, 2000);
                        },
                        error: function(response) {
                            Swal.fire({
                                icon: "error",
                                title: 'Delete <div class="text-blue-500">'+ nameProduct +'</div> failed!',
                                showConfirmButton: false,
                                timer: 2000,
                                timerProgressBar: true,
                            });
                        }

                    });
                }
            });
        });
    });
</script>
